```javascript
function check_two_strings(s1, s2) {
    var count = 0;
    var length = s1.length;
    var char1 = null, char2 = null;
    for (var i = 0; i < length; i++) {
        if (s1[i] != s2[i]) {
            count += 1;
            if ((count == 2 && (s1[i] != char2 || s2[i] != char1)) || count > 2) {
                return false;
            }
            char1 = s1[i];
            char2 = s2[i];
        }
    }
    return count == 0 || count == 2;
}
