const f_gold = (phrases) => {
    const same_first_word = new Map();
    for (let i = 0; i < phrases.length; i++) {
        const phrase = phrases[i];
        const first_word = phrase.split(" ")[0];
        if (!same_first_word.has(first_word)) {
            same_first_word.set(first_word, new Set());
        }
        same_first_word.get(first_word).add(i);
    }
    const res = new Set();
    for (let i = 0; i < phrases.length; i++) {
        const phrase = phrases[i];
        const words = phrase.split(" ");
        const last_word = words[words.length - 1];
        if (same_first_word.has(last_word)) {
            for (const j of same_first_word.get(last_word)) {
                if (i !== j) {
                    res.add(words.slice(0, -1).concat(phrases[j].split(" ")).join(" "));
                }
            }
        }
    }
    return Array.from(res).sort();
};
