// define a function to calculate the minimum speed required to arrive on time
function f_gold(dist, hour) {
    // define a nested function to check if a given speed allows arrival on time
    function arrive_on_time(speed) {
        var res = 0;
        // iterate through the distances and calculate the time required to travel each distance
        for (var i = 0; i < dist.length; i++) {
            res += (i == dist.length - 1) ? (dist[i] / speed) : Math.ceil(dist[i] / speed);
        }
        // return true if the total time required is less than or equal to the given hour
        return res <= hour;
    }
    // set the initial range of speeds to check
    var left = 1;
    var right = 10 ** 7;
    // perform binary search to find the minimum speed
    while (left < right) {
        var mid = (left + right) >> 1;
        if (arrive_on_time(mid)) {
            right = mid;
        }
        else {
            left = mid + 1;
        }
    }
    // return the minimum speed if it allows arrival on time, otherwise return -1
    return arrive_on_time(left) ? left : -1;
}
