"use strict";

function f_gold(price, special, needs) {
    function total(price, needs) {
        let sum = 0;
        for (let i = 0; i < needs.length; i++) {
            sum += price[i] * needs[i];
        }
        return sum;
    }
    let ans = total(price, needs);
    let t = [];
    for (let offer of special) {
        t = [];
        for (let j = 0; j < needs.length; j++) {
            if (offer[j] > needs[j]) {
                t = [];
                break;
            }
            t.push(needs[j] - offer[j]);
        }
        if (t.length > 0) {
            ans = Math.min(ans, offer[offer.length - 1] + f_gold(price, special, t));
        }
    }
    return ans;
}
