function find_longest_working_period(hours_worked) {
    let longest_period = 0;
    let current_balance = 0;
    let balance_history = {};
    for (let i = 0; i < hours_worked.length; i++) {
        current_balance += (hours_worked[i] > 8) ? 1 : -1;
        if (current_balance > 0) {
            longest_period = i + 1;
        }
        else {
            if (!(current_balance in balance_history)) {
                balance_history[current_balance] = i;
            }
            if (current_balance - 1 in balance_history) {
                longest_period = Math.max(longest_period, i - balance_history[current_balance - 1]);
            }
        }
    }
    return longest_period;
}
```
