function find_minimum_cost(n) {
    var dp = Array(n + 10).fill(0).map(() => Array(n + 10).fill(0));
    for (var length = 2; length < n + 1; length++) {
        for (var i = 1; i < n - length + 2; i++) {
            var j = i + length - 1;
            dp[i][j] = Number.POSITIVE_INFINITY;
            for (var k = i; k < j + 1; k++) {
                var t = Math.max(dp[i][k - 1], dp[k + 1][j]) + k;
                dp[i][j] = Math.min(dp[i][j], t);
            }
        }
    }
    return dp[1][n];
}
