function check_strings(s, goal) {
    var s_length = s.length;
    var goal_length = goal.length;
    if (s_length != goal_length) {
        return false;
    }
    var diff_count = 0;
    for (var i = 0; i < goal_length; i++) {
        if (s[i] != goal[i]) {
            diff_count++;
        }
