```javascript
function calculateScore(operations) {
    let stack = [];
    for (let operation of operations) {
        if (operation == '+') {
            stack.push(stack[stack.length - 1] + stack[stack.length - 2]);
        }
        else if (operation == 'D') {
            stack.push(stack[stack.length - 1] << 1);
        }
        else if (operation == 'C') {
            stack.pop();
        }
        else {
            stack.push(parseInt(operation));
        }
    }
    return stack.reduce((accumulator, currentValue) => accumulator + currentValue, 0);
}
