const itertools = require('itertools');

function f_gold(n, lights, requirement) {
    let d = new Array(100010).fill(0);
    for (let i = 0; i < lights.length; i++) {
        let p = lights[i][0];
        let r = lights[i][1];
        let j = Math.min(n - 1, p + r);
        i = Math.max(0, p - r);
        d[i] += 1;
        d[j + 1] -= 1;
    }
    let accumulated = itertools.accumulate(d);
    let result = 0;
    for (let i = 0; i < requirement.length; i++) {
        if (accumulated[i] >= requirement[i]) {
            result += 1;
        }
    }
    return result;
}
