const find_longest_substring_with_alternating_vowels = (s) => {
    let positions = new Array(32).fill(Infinity);
    positions[0] = -1;
    let vowels = 'aeiou';
    let state = 0, answer = 0;
    for (let i = 0; i < s.length; i++) {
        for (let j = 0; j < vowels.length; j++) {
            if (s[i] == vowels[j]) {
                state ^= 1 << j;
            }
        }
        answer = Math.max(answer, i - positions[state]);
        positions[state] = Math.min(positions[state], i);
    }
    return answer;
}
```
