function findQuietestRichestPerson (richer, quiet) {
    var n = quiet.length;
    var graph = {};
    for (var i = 0; i < richer.length; i++) {
        var richerPerson = richer[i][0];
        var poorerPerson = richer[i][1];
        if (graph[poorerPerson] == undefined) {
            graph[poorerPerson] = [];
        }
        graph[poorerPerson].push(richerPerson);
    }
    var answer = new Array(n).fill(-1);
    function dfs (i) {
        if (answer[i] != -1) {
            return;
        }
        answer[i] = i;
        for (var j = 0; j < graph[i]?.length; j++) {
            dfs(graph[i][j]);
            if (quiet[answer[graph[i][j]]] < quiet[answer[i]]) {
                answer[i] = answer[graph[i][j]];
            }
        }
    }
    for (var i = 0; i < n; i++) {
        dfs(i);
    }
    return answer;
}
