const { Deque } = require('double-ended-queue');

function ladderLength(beginWord, endWord, wordList) {
    function extend(m1, m2, q) {
        for (let i = 0; i < q.length; i++) {
            const s = q.shift();
            const step = m1.get(s);
            const sArr = s.split('');
            for (let j = 0; j < sArr.length; j++) {
                const ch = sArr[j];
                for (let k = 0; k < 26; k++) {
                    sArr[j] = String.fromCharCode(97 + k);
                    const t = sArr.join('');
                    if (m1.has(t) || !words.has(t)) {
                        continue;
                    }
                    if (m2.has(t)) {
                        return step + 1 + m2.get(t);
                    }
                    m1.set(t, step + 1);
                    q.push(t);
                }
                sArr[j] = ch;
            }
        }
        return -1;
    }

    const words = new Set(wordList);
    if (!words.has(endWord)) {
        return 0;
    }
    const q1 = new Deque([beginWord]);
    const q2 = new Deque([endWord]);
    const m1 = new Map([[beginWord, 0]]);
    const m2 = new Map([[endWord, 0]]);
    while (q1.length > 0 && q2.length > 0) {
        const t = q1.length <= q2.length ? extend(m1, m2, q1) : extend(m2, m1, q2);
        if (t !== -1) {
            return t + 1;
        }
    }
    return 0;
}
