function findSubarrayWithEqualSum(nums) {
    let length = nums.length;
    let prefixSum = new Array(length + 1).fill(0);
    for (let i = 0; i < nums.length; i++) {
        prefixSum[i + 1] = prefixSum[i] + nums[i];
    }
    for (let j = 3; j < length - 3; j++) {
        let seenPrefixSums = new Set();
        for (let i = 1; i < j - 1; i++) {
            if (prefixSum[i] == prefixSum[j] - prefixSum[i + 1]) {
                seenPrefixSums.add(prefixSum[i]);
            }
        }
        for (let k = j + 2; k < length - 1; k++) {
            if (prefixSum[length] - prefixSum[k + 1] == prefixSum[k] - prefixSum[j + 1] && seenPrefixSums.has(prefixSum[length] - prefixSum[k + 1])) {
                return true;
            }
        }
    }
    return false;
}
