function fill_missing_values_with_zero(arr) {
    var length = arr.length;
    var index = -1;
    var count = 0;
    while (count < length) {
        index += 1;
        count += (arr[index]) ? 1 : 2;
    }
    var last_index = length - 1;
    if (count == length + 1) {
        arr[last_index] = 0;
        index = index - 1;
        last_index = last_index - 1;
    }
    while (~last_index) {
        if (arr[index] == 0) {
            arr[last_index] = arr[last_index - 1] = arr[index];
            last_index -= 1;
        }
        else {
            arr[last_index] = arr[index];
        }
        index = index - 1;
        last_index = last_index - 1;
    }
}
