```javascript
function calculate_sum_of_degrees(n, roads) {
    var degrees = Array(n).fill(0);
    for (var i = 0; i < roads.length; i++) {
        degrees[roads[i][0]] += 1;
        degrees[roads[i][1]] += 1;
    }
    degrees.sort((a, b) => a - b);
    var sum = 0;
    for (var i = 0; i < degrees.length; i++) {
        sum += (i + 1) * degrees[i];
    }
    return sum;
}
