function compareSentences(sentenceOne, sentenceTwo) {
    if (sentenceOne == sentenceTwo) {
        return true;
    }
    var lengthOne = sentenceOne.length;
    var lengthTwo = sentenceTwo.length;
    if (lengthOne == lengthTwo) {
        return false;
    }
    if (lengthOne < lengthTwo) {
        [sentenceOne, sentenceTwo] = [sentenceTwo, sentenceOne];
    }
    var wordsOne = sentenceOne.split(/\s+/);
    var wordsTwo = sentenceTwo.split(/\s+/);
    var i = 0;
    var j = 0;
    var lengthWordsOne = wordsOne.length;
    var lengthWordsTwo = wordsTwo.length;
    while (i < lengthWordsTwo && wordsOne[i] == wordsTwo[i]) {
        i++;
    }
    if (i == lengthWordsTwo) {
        return true;
    }
    while (j < lengthWordsTwo && wordsOne[lengthWordsOne - 1 - j] == wordsTwo[lengthWordsTwo - 1 - j]) {
        j++;
    }
    return j == lengthWordsTwo || i + j == lengthWordsTwo;
}
