function compare_versions(version1, version2) {
    var i = 0;
    var j = 0;
    var length1 = version1.length;
    var length2 = version2.length;
    while (i < length1 || j < length2) {
        var num1 = 0;
        var num2 = 0;
        while (i < length1 && version1[i] != '.') {
            num1 = num1 * 10 + parseInt(version1[i]);
            i += 1;
        }
        while (j < length2 && version2[j] != '.') {
            num2 = num2 * 10 + parseInt(version2[j]);
            j += 1;
        }
        if (num1 != num2) {
            return (num1 < num2) ? -1 : 1;
        }
        i += 1;
        j += 1;
    }
    return 0;
}
