// function to calculate the minimum number of flips required to make all 0's to 1's and vice versa
function f_gold(s) {
    let n = s.length;
    // initialize arrays to store the number of 1's on the left and the number of 0's on the right
    let left = new Array(n + 1).fill(0);
    let right = new Array(n + 1).fill(0);
    // initialize ans to a large number
    let ans = 0x3F3F3F3F;
    // calculate the number of 1's on the left for each index
    for (let i = 1; i < n + 1; i++) {
        left[i] = left[i - 1] + (s[i - 1] == '1' ? 1 : 0);
    }
    // calculate the number of 0's on the right for each index
    for (let i = n - 1; i > -1; i--) {
        right[i] = right[i + 1] + ((s[i] == '0') ? 1 : 0);
    }
    // calculate the minimum number of flips required for each index and update ans accordingly
    for (let i = 0; i < n + 1; i++) {
        ans = Math.min(ans, left[i] + right[i]);
    }
    // return the minimum number of flips required
    return ans;
}
