        }
        if (code.substring(i, i + 9) == '<![CDATA[') {
            i = code.indexOf(']]>', i + 9);
            if (i < 0) {
                return false;
            }
            i += 2;
        }
        else if (code.substring(i, i + 2) == '</') {
            var j = i + 2;
            i = code.indexOf('>', j);
            if (i < 0) {
                return false;
            }
            var tag = code.substring(j, i);
            if (!is_valid_tag(tag) || !stack.length || stack.pop() != tag) {
                return false;
            }
        }
        else if (code[i] == '<') {
            var j = i + 1;
            i = code.indexOf('>', j);
            if (i < 0) {
                return false;
            }
            var tag = code.substring(j, i);
            if (!is_valid_tag(tag)) {
                return false;
            }
            stack.push(tag);
        }
        i += 1;
    }
    return !stack.length;
}
