// function to count the number of subarrays whose sum is at least 'goal'
function f_gold(nums, goal) {
    // initialize variables
    var i1 = 0, i2 = 0, s1 = 0, s2 = 0, j = 0, ans = 0;
    var n = nums.length;
    // iterate through the array
    while (j < n) {
        s1 += nums[j];
        s2 += nums[j];
        // move the left pointer i1 to the right if the sum is greater than 'goal'
        while (i1 <= j && s1 > goal) {
            s1 -= nums[i1];
            i1 += 1;
        }
        // move the left pointer i2 to the right if the sum is greater than or equal to 'goal'
        while (i2 <= j && s2 >= goal) {
            s2 -= nums[i2];
            i2 += 1;
        }
        // add the number of subarrays whose sum is at least 'goal'
        ans += i2 - i1;
        j += 1;
    }
    // return the count of subarrays
    return ans;
}
