function find_maximum_subarray_length(nums, k) {
    var sum_index_map = { 0: -1 };
    var current_sum = 0, max_subarray_length = 0;
    for (var i = 0; i < nums.length; i++) {
        current_sum += nums[i];
        if (current_sum - k in sum_index_map) {
            max_subarray_length = Math.max(max_subarray_length, i - sum_index_map[current_sum - k]);
        }
        if (!(current_sum in sum_index_map)) {
            sum_index_map[current_sum] = i;
        }
    }
    return max_subarray_length;
}
