function rotateGrid(grid, startRow, endRow, startCol, endCol, rotations) {
    var tempArray = [];
    for (var j = endCol; j > startCol; j--) {
        tempArray.push(grid[startRow][j]);
    }
    for (var i = startRow; i < endRow; i++) {
        tempArray.push(grid[i][startCol]);
    }
    for (var j = startCol; j < endCol; j++) {
        tempArray.push(grid[endRow][j]);
    }
    for (var i = endRow; i > startRow; i--) {
        tempArray.push(grid[i][endCol]);
    }
    rotations %= tempArray.length;
    tempArray = tempArray.slice(-rotations).concat(tempArray.slice(0, -rotations));
    rotations = 0;
    for (var j = endCol; j > startCol; j--) {
        grid[startRow][j] = tempArray[rotations];
        rotations++;
    }
    for (var i = startRow; i < endRow; i++) {
        grid[i][startCol] = tempArray[rotations];
        rotations++;
    }
    for (var j = startCol; j < endCol; j++) {
        grid[endRow][j] = tempArray[rotations];
        rotations++;
    }
    for (var i = endRow; i > startRow; i--) {
        grid[i][endCol] = tempArray[rotations];
        rotations++;
    }
}

function rotateMatrix(grid, rotations) {
    var numRows = grid.length;
