// function to check if two strings differ by at most 2 characters
function f_gold(s1, s2) {
    var cnt = 0; // count of different characters
    var n = s1.length; // length of the strings
    var c1 = null, c2 = null; // characters at different positions
    for (var i = 0; i < n; i++) {
        if (s1[i] != s2[i]) { // if characters at position i are different
            cnt += 1; // increment the count of different characters
            if ((cnt == 2 && (s1[i] != c2 || s2[i] != c1)) || cnt > 2) { // if more than 2 characters are different or the different characters are not adjacent
                return false; // return false
            }
            c1 = s1[i]; // store the first different character
            c2 = s2[i]; // store the second different character
        }
    }
    return cnt == 0 || cnt == 2; // return true if there are no different characters or exactly 2 different characters
}
