// function to find the subarray of length 3k with maximum sum
function f_gold(nums, k) {
    // initialize variables
    var s = 0, s1 = 0, s2 = 0, s3 = 0;
    var mx1 = 0, mx12 = 0;
    var idx1 = 0, idx12 = 0;
    var ans = [];
    // loop through the array
    for (var i = k * 2; i < nums.length; i++) {
        // update sums
        s1 += nums[i - k * 2];
        s2 += nums[i - k];
        s3 += nums[i];
        // check if we have a subarray of length 3k
        if (i >= k * 3 - 1) {
            // update maximum sum of subarray of length k
            if (s1 > mx1) {
                mx1 = s1;
                idx1 = i - k * 3 + 1;
            }
            // update maximum sum of subarray of length 2k
            if (mx1 + s2 > mx12) {
                mx12 = mx1 + s2;
                idx12 = [idx1, i - k * 2 + 1];
            }
            // update maximum sum of subarray of length 3k
            if (mx12 + s3 > s) {
                s = mx12 + s3;
                ans = [...idx12, i - k + 1];
            }
            // remove the first element of each subarray
            s1 -= nums[i - k * 3 + 1];
            s2 -= nums[i - k * 2 + 1];
            s3 -= nums[i - k + 1];
        }
    }
    // return the subarray with maximum sum
    return ans;
}
