```javascript
// function to count the number of elements in the input array that are less than each element
function f_gold(nums) {
    // initialize an array of size 101 with all elements set to 0
    let cnt = Array(101).fill(0);
    // count the frequency of each element in the input array
    for (let num of nums) {
        cnt[num] += 1;
    }
    // calculate the cumulative frequency of each element in the input array
    for (let i = 1; i < 101; i++) {
        cnt[i] += cnt[i - 1];
    }
    // create a new array with the count of elements less than each element in the input array
    let res = [];
    for (let num of nums) {
        res.push(0 === num ? 0 : cnt[num - 1]);
    }
    // return the new array
    return res;
}
```
