```javascript
// sort the array of beans in ascending order
// calculate the sum of all elements in the array
// initialize variables to store the sum and length of the array
function f_gold(beans) {
    beans.sort((a, b) => a - b);
    var ans = beans.reduce((a, b) => a + b, 0);
    var s = ans;
    var n = beans.length;
    // iterate through the array and calculate the minimum sum
    for (var i = 0; i < beans.length; i++) {
        ans = Math.min(ans, s - beans[i] * (n - i));
    }
    // return the minimum sum
    return ans;
}
```
