// function to check if it is possible to reach a 0 value in the array from a given start index
function f_gold(arr, start) {
    var n = arr.length;
    var q = [start];
    while (q.length > 0) {
        var i = q.shift();
        if (arr[i] == 0) {
            return true;
        }
        // add the indices that can be reached from i to the queue
        for (var j of [i + arr[i], i - arr[i]]) {
            if (0 <= j && j < n && arr[j] >= 0) {
                q.push(j);
            }
        }
        // mark the current index as visited
        arr[i] = -1;
    }
    // if no 0 value can be reached from start index, return false
    return false;
}
