function find_top_values(values_array, labels_array, num_wanted_values, use_limit) {
    var arr = Array.from(values_array, (value, index) => [value, labels_array[index]]);
    arr.sort(function (a, b) { return b[0] - a[0]; });
    var count = {};
    var num = 0;
    var answer = num;
    for (var i = 0; i < arr.length; i++) {
        var value = arr[i][0];
        var label = arr[i][1];
        if (count[label] == null || count[label] < use_limit) {
            if (count[label] == null) count[label] = 0;
            count[label] += 1;
            num += 1;
            answer += value;
        }
        if (num == num_wanted_values) break;
    }
    return answer;
}
