function f_gold(x, y, bound) {
    var s = new Set();
    var i = 1;
    while (i < bound) {
        var j = 1;
        while (j < bound) {
            if (i + j <= bound) {
                s.add(i + j);
            }
            if (y == 1) {
                break;
            }
            j *= y;
        }
        if (x == 1) {
            break;
        }
        i *= x;
    }
    return Array.from(s).sort((a, b) => a - b);
}


