```javascript
// define a function that converts a column number to its corresponding Excel column name
function f_gold(columnNumber) {
    var res = [];
    // loop until columnNumber is 0
    while (columnNumber) {
        columnNumber -= 1;
        // get the ASCII character code of the corresponding letter and push it to the res array
        res.push(String.fromCharCode(65 + columnNumber % 26));
        // update columnNumber to the next quotient
        columnNumber = Math.floor(columnNumber / 26);
    }
    // reverse the res array and join its elements to form the Excel column name
    return res.reverse().join('');
}
```
