function findOptimalArrows(numTotalArrows, aliceArrows) {
    let n = aliceArrows.length;
    let optimalState = 0;
    let maxPoints = -1;
    for (var mask = 0; mask < (1 << n); mask++) {
        let cnt = 0, points = 0;
        for (let i = 0; i < aliceArrows.length; i++) {
            if ((mask >> i) & 1) {
                cnt += aliceArrows[i] + 1;
                points += i;
            }
        }
        if (cnt <= numTotalArrows && maxPoints < points) {
            optimalState = mask;
            maxPoints = points;
        }
    }
    var optimalArrows = Array(aliceArrows.length).fill(0);
    for (let i = 0; i < aliceArrows.length; i++) {
        if ((optimalState >> i) & 1) {
            optimalArrows[i] = aliceArrows[i] + 1;
            numTotalArrows -= optimalArrows[i];
        }
    }
    optimalArrows[0] = numTotalArrows;
    return optimalArrows;
}
