function findSpiralMatrix(rows, cols, startRow, startCol) {
    var spiralMatrix = [[startRow, startCol]];
    if (rows * cols == 1) {
        return spiralMatrix;
    }
    var k = 1;
    while (true) {
        for (var [deltaRow, deltaCol, deltaK] of [[0, 1, k], [1, 0, k], [0, -1, k + 1], [-1, 0, k + 1]]) {
            for (var _ = 0; _ < deltaK; _++) {
                startRow += deltaRow;
                startCol += deltaCol;
                if (0 <= startRow && startRow < rows && 0 <= startCol && startCol < cols) {
                    spiralMatrix.push([startRow, startCol]);
                    if (spiralMatrix.length == rows * cols) {
                        return spiralMatrix;
                    }
                }
            }
        }
        k += 2;
    }
}
