            vis.add((a, b))
            q.append((a, b))
    n = len(grid)
    target = (n * n - 2, n * n - 1)
    q = deque([(0, 1)])
    vis = {(0, 1)}
    ans = 0
    while q:
        for _ in range(len(q)):
            a, b = q.popleft()
            if (a, b) == target:
                return ans
            i1, j1 = a // n, a % n
            i2, j2 = b // n, b % n
            if (
                j1 + 1 < n
                and j2 + 1 < n
                and grid[i1][j1 + 1] == 0
                and grid[i2][j2 + 1] == 0
            ):
                check(i1 * n + j1 + 1, i2 * n + j2 + 1)
