// function to find the shortest path between two points in a grid
function f_gold(grid) {
    // helper function to add new points to the queue
    function check(a, b) {
        if (!vis.has(a + "," + b)) {
            vis.add(a + "," + b);
            q.push([a, b]);
        }
    }
    // initialize variables
    var n = grid.length;
    var target = [n * n - 2, n * n - 1];
    var q = [[0, 1]];
    var vis = new Set([0 + "," + 1]);
    var ans = 0;
    // loop until queue is empty
    while (q.length > 0) {
        // loop through all points in the queue
        for (var _ = 0, ql = q.length; _ < ql; _++) {
            var [a, b] = q.shift();
            // if target is reached, return the answer
            if (a == target[0] && b == target[1]) return ans;
            // get coordinates of the two points
            var i1 = Math.floor(a / n);
            var j1 = a % n;
            var i2 = Math.floor(b / n);
            var j2 = b % n;
            // check if points can move right or down
            if (j1 + 1 < n && j2 + 1 < n && grid[i1][j1 + 1] == 0 && grid[i2][j2 + 1] == 0) {
                check(i1 * n + j1 + 1, i2 * n + j2 + 1);
                if (j1 == j2) check(a, i1 * n + j2 + 1);
            }
            if (i1 + 1 < n && i2 + 1 < n && grid[i1 + 1][j1] == 0 && grid[i2 + 1][j2] == 0) {
                check((i1 + 1) * n + j1, (i2 + 1) * n + j2);
                if (i1 == i2) check(a, (i2 + 1) * n + j1);
            }
        }
        ans++;
    }
    // if target is not reachable, return -1
    return -1;
}
