// This function takes a graph represented as an array of arrays and returns an array of nodes with no outgoing edges
function f_gold(graph) {
    let n = graph.length;
    // calculate the out-degree of each node
    let outDegree = graph.map(vs => vs.length);
    // create a reversed graph
    let revGraph = {};
    for (let u = 0; u < graph.length; u++) {
        for (let v of graph[u]) {
            if (!revGraph[v]) revGraph[v] = [];
            revGraph[v].push(u);
        }
    }
    // find nodes with no outgoing edges using a queue
    let q = [...Array(n).keys()].filter(i => outDegree[i] == 0);
    while (q.length > 0) {
        for (let u of revGraph[q.shift()] ?? []) {
            outDegree[u] -= 1;
            if (outDegree[u] == 0) {
                q.push(u);
            }
        }
    }
    // return an array of nodes with no outgoing edges
    return [...Array(n).keys()].filter(i => outDegree[i] == 0);
}
