const countPairs = (n, edges) => {
    const dfs = (i) => {
        vis[i] = true;
        let res = 1;
        for (let j of g[i]) {
            if (!vis[j]) {
                res += dfs(j);
            }
        }
        return res;
    };
    const g = new Map();
    for (let [a, b] of edges) {
        if (!g.has(a)) {
            g.set(a, []);
        }
        if (!g.has(b)) {
            g.set(b, []);
        }
        g.get(a).push(b);
        g.get(b).push(a);
    }
    const vis = new Array(n).fill(false);
    const arr = [];
    for (let i = 0; i < n; i++) {
        if (!vis[i]) {
            arr.push(dfs(i));
        }
    }
    let ans = 0, t = 0;
    for (let v of arr) {
        t += v;
        ans += v * (n - t);
    }
    return ans;
};
