function find_number_of_pairs(n, edges) {
    function depth_first_search(i) {
        visited[i] = true;
        var result = 1;
        for (var j = 0; j < graph[i]?.length; j++) {
            if (!visited[graph[i][j]]) {
                result += depth_first_search(graph[i][j]);
            }
        }
        return result;
    }
    var graph = {};
    for (var i = 0; i < edges.length; i++) {
        if (!graph[edges[i][0]]) {
            graph[edges[i][0]] = [];
        }
        if (!graph[edges[i][1]]) {
            graph[edges[i][1]] = [];
        }
        graph[edges[i][0]].push(edges[i][1]);
        graph[edges[i][1]].push(edges[i][0]);
    }
    var visited = [];
    for (var i = 0; i < n; i++) {
        visited.push(false);
    }
    var connected_components = [];
    for (var i = 0; i < n; i++) {
        if (!visited[i]) {
            connected_components.push(depth_first_search(i));
        }
    }
    var ans = 0;
    var total_sum = 0;
    for (var i = 0; i < connected_components.length; i++) {
        total_sum += connected_components[i];
        ans += connected_components[i] * (n - total_sum);
    }
    return ans;
}
