function f_gold (richer, quiet) {
    var n = quiet.length;
    var g = {};
    for (var i = 0; i < richer.length; i++) {
        var a = richer[i][0];
        var b = richer[i][1];
        if (g[b] == undefined) {
            g[b] = [];
