// a recursive function that returns the maximum value of an array
function f_gold(nums) {
    var n = nums.length;
    // if the array has only one element, return it
    if (n == 1) {
        return nums[0];
    }
    // create a new array t and push the maximum or minimum value of each pair of adjacent elements in nums
    var t = [];
    for (var i = 0; i < (n >> 1); i++) {
        var v = ((i & 1) ?
            Math.max(nums[i << 1], nums[i << 1 | 1]) :
            Math.min(nums[i << 1], nums[i << 1 | 1]));
        t.push(v);
    }
    // recursively call f_gold with the new array t
    return f_gold(t);
}
