function find_minimum_moves_to_reach_end(board) {
    function get_coordinates(x) {
        let row = Math.floor((x - 1) / n);
        let col = (x - 1) % n;
        if (row & 1) {
            col = n - 1 - col;
        }
        return [n - 1 - row, col];
    }
    let n = board.length;
    let queue = [1];
    let visited = {1: true};
    let moves = 0;
    while (queue.length > 0) {
        for (let i = 0; i < queue.length; i++) {
            let current = queue.shift();
            if (current == n * n) {
                return moves;
            }
            for (let next = current + 1; next < Math.min(current + 7, n * n + 1); next++) {
                let [row, col] = get_coordinates(next);
                if (board[row] && board[row][col] != -1) {
                    next = board[row][col];
                }
                if (!visited[next]) {
                    queue.push(next);
                    visited[next] = true;
                }
            }
        }
        moves += 1;
    }
    return -1;
}
