from typing import *
def f_gold(matrix: List[List[int]]) -> int:
    @cache
    def dfs(i, j):
        ans = 1
        for a, b in [[-1, 0], [1, 0], [0, 1], [0, -1]]:
            x, y = i + a, j + b
            if 0 <= x < m and 0 <= y < n and matrix[x][y] > matrix[i][j]:
