function cache(f) { return f; }
function f_gold(matrix) {
    function dfs(i, j) {
        let ans = 1;
        for (let [a, b] of [[-1, 0], [1, 0], [0, 1], [0, -1]]) {
            let x = i + a, y = j + b;
            if (0 <= x && x < m && 0 <= y && y < n && matrix[x][y] > matrix[i][j]) {
                ans = Math.max(ans, dfs(x, y) + 1);
            }
        }
        return ans;
    }
    const m = matrix.length, n = matrix[0].length;
    return Math.max(...Array.from({length: m}, (_, i) => Array.from({length: n}, (_, j) => dfs(i, j)).flat()));
}
