```javascript
// function to generate a row of Pascal's triangle
function f_gold(rowIndex) {
    // create an array of length rowIndex+1 and fill it with 1's
    var row = Array(rowIndex + 1).fill(1);
    // loop through each row and calculate the values
    for (var i = 2; i <= rowIndex; i++) {
        for (var j = i - 1; j > 0; j--) {
            // calculate the value of the current element by adding the two elements above it
            row[j] += row[j - 1];
        }
    }
    // return the row
    return row;
}
```
