```javascript
// function to calculate the number of streets that meet the light requirements
const f_gold = (n, lights, requirement) => {
    // initialize an array with 100010 elements, all set to 0
    let d = Array(100010).fill(0);
    // loop through each light and update the array d
    for (let [p, r] of lights) {
        // calculate the range of the light
        let i = Math.max(0, p - r);
        let j = Math.min(n - 1, p + r);
        // update the array d
        d[i] += 1;
        d[j + 1] -= 1;
    }
    // calculate the cumulative sum of the array d and check if it meets the requirement
    return d.map((sum => v => sum += v)(0)).map((v, i) => v >= requirement[i]).reduce((a, b) => a + b);
};
```
