// function to find the maximum values of three integers in an array of triplets
function f_gold(triplets, target) {
    // initialize the maximum values of a, b, and c to 0
    var maxA = 0, maxB = 0, maxC = 0;
    // extract the target values for a, b, and c
    var [tA, tB, tC] = target;
    // loop through each triplet in the array
    for (var i = 0; i < triplets.length; i++) {
        // extract the values of a, b, and c from the current triplet
        var a = triplets[i][0];
        var b = triplets[i][1];
        var c = triplets[i][2];
        // check if the values of a, b, and c are less than or equal to the target values
        if (a <= tA && b <= tB && c <= tC) {
            // update the maximum values of a, b, and c if the current values are greater
            maxA = Math.max(maxA, a);
            maxB = Math.max(maxB, b);
            maxC = Math.max(maxC, c);
        }
    }
    // check if the maximum values of a, b, and c are equal to the target values
    return maxA == tA && maxB == tB && maxC == tC;
}
