function convertRomanToDecimal(romanNumeral) {
    let romanNumerals = {
        'M': 1000,
        'CM': 900,
        'D': 500,
        'CD': 400,
        'C': 100,
        'XC': 90,
        'L': 50,
        'XL': 40,
        'X': 10,
        'IX': 9,
        'V': 5,
        'IV': 4,
        'I': 1,
    };
    var index = 0, decimalValue = 0;
    while (index < romanNumeral.length) {
        if (index + 1 < romanNumeral.length && romanNumeral.substring(index, index + 2) in romanNumerals) {
            decimalValue += romanNumerals[romanNumeral.substring(index, index + 2)];
            index += 2;
        } else {
            decimalValue += romanNumerals[romanNumeral.substring(index, index + 1)];
            index += 1;
        }
    }
    return decimalValue;
}
