function find_anagrams_in_string(string, pattern) {
    let pattern_counter = new Map();
    for (let i = 0; i < pattern.length; i++) {
        if (pattern_counter.has(pattern[i])) {
            pattern_counter.set(pattern[i], pattern_counter.get(pattern[i]) + 1);
        } else {
            pattern_counter.set(pattern[i], 1);
        }
    }
    let result = [];
    let left = 0;
    let right = 0;
    let string_counter = new Map();
    while (right < string.length) {
        if (string_counter.has(string[right])) {
            string_counter.set(string[right], string_counter.get(string[right]) + 1);
        } else {
            string_counter.set(string[right], 1);
        }
        while ((string_counter.has(string[right]) ? string_counter.get(string[right]) : 0) > (pattern_counter.has(string[right]) ? pattern_counter.get(string[right]) : 0)) {
            string_counter.set(string[left], (string_counter.has(string[left]) ? string_counter.get(string[left]) : 0) - 1);
            left += 1;
        }
        if (right - left + 1 == pattern.length) {
            result.push(left);
        }
        right += 1;
    }
    return result;
}
