// define a function that takes a sentence as input and returns the number of words that meet certain criteria
function f_gold(sentence) {
    // define a helper function that checks if a given word meets the criteria
    function check(token) {
        var hyphen = false;
        for (var i = 0; i < token.length; i++) {
            var c = token[i];
            if (c.match(/^\d+$/) || ('!.,'.includes(c) && i < token.length - 1)) {
                return false;
            }
            if (c == '-') {
                if (hyphen || i == 0 || i == token.length - 1 || token[i - 1].toLowerCase() !== token[i - 1] || token[i + 1].toLowerCase() !== token[i + 1]) {
                    return false;
                }
                hyphen = true;
            }
        }
        return true;
    }
    // split the sentence into words, check each word, and return the count of words that meet the criteria
    return sentence.split(/\s+/).map(check).reduce((a, b) => a + b, 0);
}
