function format_time(time_string) {
    var time_array = time_string.split("");
    if (time_array[0] == '?') {
        time_array[0] = ('4' <= time_array[1] <= '9') ? '1' : '2';
    }
    if (time_array[1] == '?') {
        time_array[1] = (time_array[0] == '2') ? '3' : '9';
    }
    if (time_array[3] == '?') {
        time_array[3] = '5';
    }
    if (time_array[4] == '?') {
        time_array[4] = '9';
    }
    return time_array.join("");
}
```
