// define a function that takes an array of moves as input and returns the winner of a tic-tac-toe game
function f_gold(moves) {
    let n = moves.length;
    // initialize an array to keep track of the number of moves made by each player in each row and column, as well as each diagonal
    let counter = [0, 0, 0, 0, 0, 0, 0, 0];
    // iterate over the moves in reverse order
    for (let i = n - 1; i >= 0; i -= 2) {
        let row = moves[i][0];
        let col = moves[i][1];
        // update the counter array based on the current move
        counter[row] += 1;
        counter[col + 3] += 1;
        if (row == col) counter[6] += 1;
        if (row + col == 2) counter[7] += 1;
        // check if any player has won the game
        if (
            counter[row] == 3 ||
            counter[col + 3] == 3 ||
            counter[6] == 3 ||
            counter[7] == 3
        )
            return (i % 2) == 0 ? "A" : "B";
    }
    // if no player has won and all moves have been made, return "Draw"; otherwise, return "Pending"
    return n == 9 ? "Draw" : "Pending";
}
